import { 
  ButtonIcon,
  Container, 
  MenuItem,
  Divider,
} from "./styles";

import HomeIcon from "../../assets/house.png";
import ShortIcon from "../../assets/shorts.png";
import SubIcon from "../../assets/subscribe.png";
import LibIcon from "../../assets/biblioteca.png";
import HistIcon from "../../assets/historico.png";
import MyVideosIcon from "../../assets/myvideos.png";
import LaterIcon from "../../assets/later.png";
import LikeIcon from "../../assets/like.png"

import { useContext } from "react";
import { MenuContext } from "../../contexts/menuContext";

const closeMenuItems = [
  {
    title: "Início",
    icon: HomeIcon,
  },
  {
    title: "Shorts",
    icon: ShortIcon,
  },
  {
    title: "Inscrição",
    icon: SubIcon,
  },
  {
    title: "Biblioteca",
    icon: LibIcon,
  },
]

const openMenuItemsFirstGroup = [
  {
    title: "Início",
    icon: HomeIcon,
  },
  {
    title: "Shorts",
    icon: ShortIcon,
  },
  {
    title: "Inscrição",
    icon: SubIcon,
  },
]

const openMenuSecondGroup = [
  {
    title: "Biblioteca",
    icon: LibIcon,
  },
  {
    title: "Histórico",
    icon: HistIcon,
  },
  {
    title: "Seus vídeos",
    icon: MyVideosIcon,
  },
  {
    title: "Assistir mais tarde",
    icon: LaterIcon,
  },
  {
    title: "Vídeos marcados com 'Gostei'",
    icon: LikeIcon,
  },
]

function Menu() {
  const { isOpenMenu } = useContext(MenuContext);

  return (
    <Container isOpenMenu={isOpenMenu} >

      {
        (!isOpenMenu) 
          ? (
            closeMenuItems.map((item, index) => (
                <MenuItem key={index} isOpenMenu={isOpenMenu}>
                  <ButtonIcon alt={item.title} src={item.icon} isOpenMenu={isOpenMenu} />
                  <span>{item.title}</span>
                </MenuItem>
              )
            )
          )
          : (
            openMenuItemsFirstGroup.map((item, index) => (
              <MenuItem key={index} isOpenMenu={isOpenMenu} title={item.title}>
                <ButtonIcon alt={item.title} src={item.icon} isOpenMenu={isOpenMenu} />
                <span>{item.title}</span>
              </MenuItem>    
            ))
          )
      } 

      {
        (!isOpenMenu)
          ? null
          : (
            <Divider />
          )
      }

      {
        (!isOpenMenu) 
        ? (
          closeMenuItems.map((item, index) => (
              <MenuItem key={index} isOpenMenu={isOpenMenu}>
                <ButtonIcon alt={item.title} src={item.icon} isOpenMenu={isOpenMenu} />
                <span>{item.title}</span>
              </MenuItem>
            )
          )
        )
        : (
          openMenuSecondGroup.map((item, index) => (
            <MenuItem key={index} isOpenMenu={isOpenMenu} title={item.title}>
              <ButtonIcon alt={item.title} src={item.icon} isOpenMenu={isOpenMenu} />
              <span>{item.title}</span>
            </MenuItem>    
          ))
        )
      }

      {
        (!isOpenMenu)
          ? null
          : (
            <Divider />
          )
      }

    </Container>
  )
}

export default Menu;
