import styled from "styled-components";

export const Container = styled.div<{ isOpenMenu: boolean }>`
  width: ${({isOpenMenu}) => isOpenMenu ? '325px' : '100px'};
  height: 100vh;
  box-sizing: border-box;
  padding: 65px 10px 10px 0px;
  display: flex;
  align-items: center;
  flex-direction: column;
  overflow-y: hidden;
  overflow-x: hidden;
  box-shadow: ${({isOpenMenu}) => isOpenMenu ? '1px 0 20px rgba(0, 0, 0, 0.1)' : 'none'};
  z-index: 1;

  &:hover {
    overflow-y: auto;
  }
`;

export const MenuItem = styled.div<{isOpenMenu: boolean}>`
  width: 90%;
  min-height: ${({isOpenMenu}) => isOpenMenu ? '40px' : '70px'};
  margin-bottom: 8px;
  border-radius: 10px;
  cursor: pointer;
  padding: 2px 17px;
  box-sizing: border-box;
  display: flex;
  flex-direction: ${({isOpenMenu}) => isOpenMenu ? 'row' : 'column'};
  align-items: center;
  justify-content: ${({isOpenMenu}) => isOpenMenu ? 'none' : 'center'};
  
  span {
    font-weight: 400;
    margin-left: ${({isOpenMenu}) => isOpenMenu ? '20px' : 'none'};
    font-size: ${({isOpenMenu}) => isOpenMenu ? '16px' : '12px'};
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
  }

  &:hover {
    background-color: #f2f2f2;
  }
`;

export const ButtonIcon = styled.img<{isOpenMenu: boolean}>`
  width: 25px;
  margin-bottom: ${({isOpenMenu}) => isOpenMenu ? 'none' : '7px'};
`;

export const Divider = styled.div`
  border-bottom: 1px solid #ddd;
  margin: 5px 0;
  width: 90%;
`;

export const OpenMenuSectionTitle = styled.h2`
  color: #0f0f0f;
  font-size: 1.3rem;
  font-weight: 400;
  margin-left: -100px;
`;

export const AvatarContainer = styled.div`
  display: flex;
  align-items: center;
  box-sizing: border-box;
  text-overflow: ellipsis;
`;

export const ImgContainer = styled.div`
  width: 24px;
  height: 24px;
  border-radius: 50%;
  border: 1px solid #000;
  margin-left: 20px;
`;

export const AvatarImg = styled.img`
  width: 100%;
  border-radius: 50%;
`;

export const AvatarName = styled.p`
  padding-left: 20px;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
`;
