import styled from "styled-components";

export const Container = styled.div<{ isOpenMenu: boolean }>`
  width: ${({isOpenMenu}) => isOpenMenu ? '300px' : '100px'};
  height: 100vh;
  box-sizing: border-box;
  padding: 65px 10px 10px 10px;
  display: flex;
  align-items: center;
  flex-direction: column;
  overflow-y: hidden;
  overflow-x: hidden;
  box-shadow: ${({isOpenMenu}) => isOpenMenu ? '1px 0 20px rgba(0, 0, 0, 0.1)' : 'none'};
  z-index: 1;

  &:hover {
    overflow-y: auto;
  }
`;


export const TooltipText = styled.div`
  background: rgba(28, 56, 151, 0.9);
  color: #fff;
  width: 200px;
  text-align: center;
  line-height: 44px;
  border-radius: 3px;
  cursor: pointer;
`;

export const TooltipBox = styled.div`
  position: absolute;
  top: calc(100% + 10px);
  left: 30px;
  visibility: hidden;
  color: transparent;
  background-color: transparent;
  width: 150px;
  padding: 5px 5px;
  border-radius: 4px;
  transition: visibility 0.5s, color 0.5s, background-color 0.5s, width 0.5s,
    padding 0.5s ease-in-out;
  &:before {
    content: "";
    width: 0;
    height: 0;
    left: 40px;
    top: -10px;
    position: absolute;
    border: 10px solid transparent;
    transform: rotate(135deg);
    transition: border 0.3s ease-in-out;
  }
`;



export const MenuItem = styled.div<{isOpenMenu: boolean}>`
  width: 90%;
  min-height: ${({isOpenMenu}) => isOpenMenu ? '40px' : '70px'};
  border-radius: 10px;
  cursor: pointer;
  padding: 2px 17px;
  box-sizing: border-box;
  display: flex;
  flex-direction: ${({isOpenMenu}) => isOpenMenu ? 'row' : 'column'};
  align-items: center;
  justify-content: ${({isOpenMenu}) => isOpenMenu ? 'none' : 'center'};

  position: relative;
  & ${TooltipText}:hover + ${TooltipBox} {
    visibility: visible;
    color: #fff;
    background-color: rgba(0, 0, 0, 0.8);
    width: 230px;
    padding: 8px 8px;
    &:before {
      border-color: transparent transparent rgba(0, 0, 0, 0.8)
        rgba(0, 0, 0, 0.8);
    }
  }
  
  span {
    font-weight: 400;
    margin-left: ${({isOpenMenu}) => isOpenMenu ? '20px' : 'none'};
    font-size: ${({isOpenMenu}) => isOpenMenu ? '14px' : '12px'};
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
  }

  &:hover {
    background-color: #f2f2f2;
  }
`;

export const ButtonIcon = styled.img<{isOpenMenu: boolean}>`
  width: 25px;
  margin-bottom: ${({isOpenMenu}) => isOpenMenu ? 'none' : '7px'};
`;
