import styled from "styled-components";

export const Container = styled.div`
  width: 100%;
  height: 30px;
  background-color: red;
  margin-top: -40px;
  margin-bottom: 30px;
  display: flex;
`;

export const Item = styled.div`
  padding: 0 12px;
  margin: 12px 12px 12px 0;
  border-radius: 10px;
`;

export const MenuItem = styled.div`
  width: 90%;
  /* min-height: ${({isOpenMenu}) => isOpenMenu ? '40px' : '70px'}; */
  margin-bottom: 8px;
  border-radius: 10px;
  cursor: pointer;
  padding: 2px 17px;
  box-sizing: border-box;
  display: flex;
  /* flex-direction: ${({isOpenMenu}) => isOpenMenu ? 'row' : 'column'}; */
  align-items: center;
  /* justify-content: ${({isOpenMenu}) => isOpenMenu ? 'none' : 'center'}; */
  
  span {
    font-weight: 400;
    /* margin-left: ${({isOpenMenu}) => isOpenMenu ? '20px' : 'none'}; */
    /* font-size: ${({isOpenMenu}) => isOpenMenu ? '16px' : '12px'}; */
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
  }

  &:hover {
    background-color: #f2f2f2;
  }
`;
