import { useContext } from "react";
import { 
  ButtonIcon,
  Container, 
  MenuItem,
} from "./styles";
import VideoIcon from "../../assets/video.png";
import HamburgerIcon from "../../assets/hamburger.png";
import SearchIcon from "../../assets/search.png";
import MicIcon from "../../assets/microfone.png";

import { MenuContext } from "../../contexts/menuContext";

const items = ['Início', 'Shorts', 'Inscrição', 'Biblioteca']
const icons = [VideoIcon, HamburgerIcon, SearchIcon, MicIcon]

function Menu() {
  const { isOpenMenu } = useContext(MenuContext);

  return (
    <Container isOpenMenu={isOpenMenu} >

      {
        items.map((item) => (
          <MenuItem key={item} isOpenMenu={isOpenMenu}>
            <ButtonIcon alt="" src={VideoIcon} />
            <span>{item}</span>
          </MenuItem>
        ))
      }

    </Container>
  )
}

export default Menu;
