import { 
  ButtonIcon,
  Container, 
  MenuItem,
} from "./styles";

import HomeIcon from "../../assets/house.png";
import ShortIcon from "../../assets/shorts.png";
import SubIcon from "../../assets/subscribe.png";
import LibIcon from "../../assets/biblioteca.png";


import { useContext } from "react";
import { MenuContext } from "../../contexts/menuContext";

const items = [
  {
    title: "Início",
    icon: HomeIcon,
  },
  {
    title: "Shorts",
    icon: ShortIcon,
  },
  {
    title: "Inscrição",
    icon: SubIcon,
  },
  {
    title: "Biblioteca",
    icon: LibIcon,
  },
  {
    title: "Histórico",
    icon: LibIcon,
  },
  {
    title: "Seus vídeos",
    icon: LibIcon,
  },
  {
    title: "Assistir mais tarde",
    icon: LibIcon,
  },
  {
    title: "Vídeos marcados com 'Gostei'",
    icon: LibIcon,
  },
]

// const { isOpenMenu } = useContext(MenuContext);

const closedMenuIcons = items.map((item, index) => {
  if (index <= 2) {
    return (
      <MenuItem key={index}>
        <ButtonIcon alt={item.title} src={item.icon} />
        <span>{item.title}</span>
      </MenuItem>
    )
  }
  return null;
})

function Menu() {
  const { isOpenMenu } = useContext(MenuContext);

  return (
    <Container isOpenMenu={isOpenMenu} >

      {
        (!isOpenMenu) 
          ? (
            closedMenuIcons
          )
          : (
            items.map((item, index) => (
              <MenuItem key={index} isOpenMenu={isOpenMenu}>
                <ButtonIcon alt={item.title} src={item.icon} isOpenMenu={isOpenMenu} />
                <span>{item.title}</span>
              </MenuItem>
            ))
          )
      }

      {/* {
        items.map((item, index) => (
          <MenuItem key={index} isOpenMenu={isOpenMenu}>
            <ButtonIcon alt={item.title} src={item.icon} isOpenMenu={isOpenMenu} />
            <span>{item.title}</span>
          </MenuItem>
        ))
      } */}

      

    </Container>
  )
}

export default Menu;
