import styled from "styled-components";

export const Container = styled.div`
  width: 100%;
  height: 30px;
  background-color: red;
  margin-top: -40px;
  margin-bottom: 30px;
  display: flex;
`;

export const Item = styled.div`
  padding: 0 12px;
  margin: 12px 12px 12px 0;
  border-radius: 10px;
  background-color: #f2f2f2;
  transition: 0.3s;

  span {
    font-weight: 400;
    font-size: 14px;
    color: #0f0f0f;
  }

  &:hover {
    background-color: #E5E5E5;
  }
`;

